mapper.sql:
FROM T_GN gn1 LEFT JOIN T_GN gn2 ON GN1.PID = GN2.GNID
WHERE 1 = 1

	AND GN1.mc like concat(concat('%',#{gn.mc}),'%')


	AND gn1.dj = #{gn.dj} 


	AND gn1.pId = #{gn.pId} 


	AND gn1.gnId = #{gn.gnId} 

ORDER BY gn1.pId

FROM T_GN gn1 LEFT JOIN T_GN gn2 ON GN1.PID = GN2.GNID
WHERE gn1.ZT = '1'

	AND GN1.pId = #{gn.gnId}
